import React from "react";

const Chat = ({ user }) => {
  return (
    <>
      <div className="card border-2 border-info w-100">
        <div className="row vh-95">
          <div className="d-flex flex-column col-12 col-lg-12 col-xl-12">
            <div className="align-items-start py-2 px-4 w-100 border-solid border-bottom border-info d-lg-block sticky-top bg-white">
              <div className="d-flex align-items-center py-1">
                <div className="position-relative">
                  <img src="" alt="" />
                </div>
                <div className="flex-grow-1">
                  <strong>Logged in as {user}</strong>
                </div>
              </div>
            </div>
            <div className="align-items-end border-info py-3 px-4 border-top d-lg-block"></div>
          </div>
        </div>
      </div>
    </>
  );
};

export default Chat;
