import React from "react";
import ChatHeader from "./ChatHeader";
import ChatInput from "./ChatInput";
import ChatContainer from "./Chatcontainer"; // corrected component name to ChatContainer

const Chat = ({ user, message, messages, sendMessage }) => {
  // corrected messages prop name
  return (
    <ChatContainer>
      {" "}
      {/* corrected component name to ChatContainer */}
      <ChatHeader user={user} />
      <div className="position-relative chat-height overflow-auto">
        <div className="d-flex flex-column p-4">
          {messages.map((message, index) => {
            return message.type === "userStatus" ? (
              <div key={index} className="text-center">
                <span className="badge bg-info">
                  {message.userId === user.userId
                    ? "You have Joined"
                    : `${message.username} has joined`}
                </span>
              </div>
            ) : (
              <div
                className={
                  message.userId === user.userId
                    ? "chat-message-right pb-4"
                    : "chat-message-left pb-4"
                }
                key={index}
              >
                <div>
                  <img
                    src=""
                    alt={message.username}
                    className="rounded-circle mr-1"
                    title={message.username}
                    width="40"
                    height="40"
                  />
                  <div className="text-muted small text-nowrap mt-2">
                    12:00 AM
                  </div>
                </div>
                <div className="flex-shrink-1 bg-light rounded py-2 px-3 ml-3">
                <div className="font-weight-blod mb-1">
                {message.userId === user.userId ? "You" : message.username}</div></div>
              </div>
            ); // added null condition for messages other than UserStatus
          })}
        </div>
      </div>
      <ChatInput message={message} sendMessage={sendMessage} />
    </ChatContainer>
  );
};

export default Chat;
